function padToTwoDigits(t){return t.toString().padStart(2,"0")}function replaceTextContent(t,e,n){t.textContent=t.textContent.replace(e,n)}function renderDate(){let t=new Date,e=t.getFullYear(),n=padToTwoDigits(t.getMonth()+1),o=padToTwoDigits(t.getDate()),a=document.getElementsByClassName("js-date");for(let t=0;t<a.length;t++){let l=a[t];replaceTextContent(l,/yyyy/g,e),replaceTextContent(l,/mm/g,n),replaceTextContent(l,/dd/g,o)}}